import './App.css';
// import { GoogleSpreadsheet } from 'google-spreadsheet';
import { useParams, useNavigate } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
// import { getRows } from '../google';
import gapi, {load} from 'gapi-client';
import axios from 'axios';

function App(){

  const { userId } = useParams();
  const navigate = useNavigate()

  const[name, setName] = useState('')
  const[amount, setAmount] = useState('')


  useEffect(() => {

    async function fetchData() {



      const params = {
        id: userId
      };



      // You can await here
      const patient = await axios.get('http://localhost:3001/patient', {params})
      // ...
      console.log(patient.data[0])
      setName(patient.data[0].name)
      setAmount(patient.data[0].owed)
    }
    fetchData()

  }, []);

  const clickQP = async (owed)=>{
    console.log(name, owed)
    try{
      const stripe = await axios.post('http://localhost:3001/patient', {name: name, amount: owed})
      console.log(stripe)
      navigate(stripe);
    }catch(err){
      console.log(err)
    }



  }








  return (
    <div className="App">
    <div className="container">
      <div className="header">
        <h2>{name}</h2>
        <h3>Patient Balance: {amount}</h3>
      </div>
      <div className="quick-pay-header">
          <h4>Quick Pay</h4>
      </div>
      <div className="button-container">
        <div className="button-row">
          <button onClick={()=> clickQP(parseFloat(amount.split("$")[1])*0.25)} className="button">25% ${parseFloat(amount.split("$")[1])*0.25}</button>
          <button onClick={()=> clickQP(parseFloat(amount.split("$")[1])*0.50)} className="button">50% ${parseFloat(amount.split("$")[1])*0.50}</button>
          <button onClick={()=> clickQP(parseFloat(amount.split("$")[1])*0.75)} className="button">75% ${parseFloat(amount.split("$")[1])*0.75}</button>
          <button onClick={()=> clickQP(parseFloat(amount.split("$")[1])*1)} className="button">100% {amount}</button>
        </div>
        <button className="long-button">Custom Amount</button>
      </div>
    </div>
    </div>
  );
}

export default App;
