import './App.css';
// import { GoogleSpreadsheet } from 'google-spreadsheet';
import { useParams } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
// import { getRows } from '../google';
import gapi, {load} from 'gapi-client';
import axios from 'axios';

function App() {


  const { userId } = useParams();

  const[name, setName] = useState('')
  const[amount, setAmount] = useState('')


  useEffect(() => {

    async function fetchData() {

      const params = {
        id: userId
      };


      // You can await here
      const patient = await axios.get('http://localhost:3001/patient', {params})
      // ...
      console.log(patient.data[0])
      setName(patient.data[0].name)
      setAmount(patient.data[0].owed)
    }
    fetchData()

  }, []);








  return (
    <div className="App">
    <div className="container">
      <div className="header">
        <h2>{name}</h2>
        <h3>Amount outstanding: {amount}</h3>
      </div>

      <div className="button-container">
        <div className="quick-pay-header">
          <h4>Quick Pay</h4>
        </div>
        <div className="button-row">
          <button className="button">$25</button>
          <button className="button">$50</button>
          <button className="button">$100</button>
          <button className="button">{amount}</button>
        </div>
        <button className="long-button">Custom Amount</button>
      </div>
    </div>
    </div>
  );
}

export default App;
